import React, { useEffect, useMemo, useRef, useState } from "react";
import { Routes, Route, useNavigate, useSearchParams } from "react-router-dom";

const API_BASE = "https://hightools-backend-production.up.railway.app";

// ---------- util: injectează Google Font ----------
function useGoogleFont(fontFamily) {
  useEffect(() => {
    if (!fontFamily) return;
    const id = "gf-" + fontFamily.replace(/\s+/g, "-").toLowerCase();
    if (document.getElementById(id)) return;

    const link = document.createElement("link");
    link.id = id;
    link.rel = "stylesheet";
    const fam = fontFamily.replace(/\s+/g, "+");
    link.href = `https://fonts.googleapis.com/css2?family=${fam}:wght@400;600;700&display=swap`;
    document.head.appendChild(link);

    return () => {
      // păstrăm fontul încărcat pentru navigări ulterioare
    };
  }, [fontFamily]);
} // ✅ aici se închide corect funcția

// ---------- Dashboard ----------
function Dashboard() {
  const navigate = useNavigate();
  const [username, setUsername] = useState(
    localStorage.getItem("hc_username") || "hyghman"
  );
  const [color, setColor] = useState(localStorage.getItem("hc_color") || "#ffffff");
  const [font, setFont] = useState(localStorage.getItem("hc_font") || "Poppins");

  const fonts = useMemo(
    () => [
      "Poppins",
      "Inter",
      "Bebas Neue",
      "Orbitron",
      "Montserrat",
      "Rubik",
      "Oswald",
      "Raleway",
      "Exo 2",
      "Work Sans",
    ],
    []
  );

  useGoogleFont(font);

  useEffect(() => {
    localStorage.setItem("hc_username", username);
    localStorage.setItem("hc_color", color);
    localStorage.setItem("hc_font", font);
  }, [username, color, font]);

  const openOverlay = () => {
    const params = new URLSearchParams({
      user: username.trim(),
      color,
      font,
    }).toString();
    window.open(`/overlay?${params}`, "_blank");
  };

  return (
    <div className="page page--dashboard">
      <h1 className="brand">HighCount</h1>

      <div className="panel">
        <div className="row">
          <label>Kick username</label>
          <input
            className="inp"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="ex: hyghman"
            spellCheck="false"
          />
        </div>

        <div className="row">
          <label>Counter color</label>
          <input
            className="color"
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
          />
          <input
            className="hex"
            value={color}
            onChange={(e) => setColor(e.target.value)}
          />
        </div>

        <div className="row">
          <label>Font</label>
          <select
            className="select"
            value={font}
            onChange={(e) => setFont(e.target.value)}
          >
            {fonts.map((f) => (
              <option key={f} value={f}>
                {f}
              </option>
            ))}
          </select>
        </div>

        <button className="cta" onClick={openOverlay}>
          Open OBS Overlay
        </button>
        <p className="hint">
          Tip: în OBS → Browser Source → pune URL-ul ferestrei deschise. Poți
          schimba oricând parametrii în bară:{" "}
          <code>?user=NAME&color=%23FFFFFF&font=Orbitron</code>
        </p>
      </div>

      <footer className="credit">
        made by{" "}
        <a
          href="https://kick.com/highman-edits"
          target="_blank"
          rel="noopener noreferrer"
          className="author"
        >
          highman_edits
        </a>{" "}
        <img
          src="https://cdn.7tv.app/emote/01GR4G94K00003C5VKDM5EYK9S/4x.avif"
          alt="emote"
          className="emote"
        />
      </footer>
    </div>
  );
}

// ---------- Overlay ----------
function Overlay() {
  const [params] = useSearchParams();
  const username = (params.get("user") || "hyghman").trim();
  const color = params.get("color") || "#FFFFFF";
  const font = params.get("font") || "Poppins";

  const [count, setCount] = useState(null);
  const [display, setDisplay] = useState(0);
  const lastCountRef = useRef(0);

  useGoogleFont(font);

  // animare între numere
  const animateTo = (from, to, duration = 800) => {
    const start = performance.now();
    const step = (t) => {
      const p = Math.min(1, (t - start) / duration);
      const eased = 1 - Math.pow(1 - p, 3); // easeOutCubic
      const val = Math.round(from + (to - from) * eased);
      setDisplay(val);
      if (p < 1) requestAnimationFrame(step);
    };
    requestAnimationFrame(step);
  };

  // fetch followers
  const fetchFollowers = async () => {
    try {
      const res = await fetch(`${API_BASE}/user?username=${username}`);
      const data = await res.json();
      if (!res.ok) throw new Error(data?.detail || "fetch error");
      const newCount = Number(data.followers_count || 0);
      setCount(newCount);

      // pornește animația doar dacă numărul s-a schimbat
      if (newCount !== lastCountRef.current) {
        animateTo(lastCountRef.current || 0, newCount);
        lastCountRef.current = newCount;
      }
    } catch {
      // nu afișăm erori pe overlay
    }
  };

  useEffect(() => {
    document.documentElement.style.setProperty("--overlay-font", font);
    document.documentElement.style.setProperty("--overlay-color", color);
  }, [font, color]);

  useEffect(() => {
    fetchFollowers();
    const id = setInterval(fetchFollowers, 5000);
    return () => clearInterval(id);
  }, [username]);

  return (
    <div className="page page--overlay">
      <div className="overlay-wrap">
        <div className="overlay-name" style={{ fontFamily: "var(--overlay-font)" }}>
          {username}
        </div>
        <div
          className="overlay-count pop"
          style={{
            color: "var(--overlay-color)",
            fontFamily: "var(--overlay-font)",
          }}
          key={display}
        >
          {display.toLocaleString()}
        </div>
        <div className="overlay-sublabel">followers</div>
      </div>

      <footer className="credit credit--overlay">
        made by{" "}
        <a
          href="https://kick.com/highman-edits"
          target="_blank"
          rel="noopener noreferrer"
          className="author"
        >
          highman_edits
        </a>{" "}
        <img
          src="https://cdn.7tv.app/emote/01GR4G94K00003C5VKDM5EYK9S/4x.avif"
          alt="emote"
          className="emote"
        />
      </footer>
    </div>
  );
}

// ---------- Router ----------
export default function App() {
  return (
    <Routes>
      <Route path="/" element={<Dashboard />} />
      <Route path="/overlay" element={<Overlay />} />
    </Routes>
  );
}
