import React, { useEffect, useState } from "react";
import "./App.css";

const API_BASE = "https://hightools-backend-production.up.railway.app";

function App() {
  // ðŸ”¹ Username din URL (?user=)
  const urlUser = new URLSearchParams(window.location.search).get("user");
  const [username, setUsername] = useState(urlUser || "hyghman");

  const [followers, setFollowers] = useState(null);
  const [error, setError] = useState("");

  // ðŸ”¹ Fetch followers count de pe backend
  const fetchFollowers = async (user) => {
    try {
      const res = await fetch(`${API_BASE}/user?username=${user}`);
      const data = await res.json();

      if (!res.ok) throw new Error(data?.detail || "Failed to fetch");

      setFollowers(data.followers_count);
      setError("");
    } catch (err) {
      setError("âš ï¸ Failed to update follower count");
    }
  };

  // ðŸ”¹ Auto-refresh la fiecare 5 secunde
  useEffect(() => {
    fetchFollowers(username);
    const interval = setInterval(() => fetchFollowers(username), 5000);
    return () => clearInterval(interval);
  }, [username]);

  return (
    <div className="overlay">
      <div className="card">
        <h1 className="username">{username}</h1>
        {followers !== null ? (
          <h2 className="count">{followers.toLocaleString()} followers</h2>
        ) : (
          <p className="loading">Loading...</p>
        )}
        {error && <p className="error">{error}</p>}
      </div>

      <footer>
        made by{" "}
        <a
          href="https://kick.com/highman-edits"
          target="_blank"
          rel="noopener noreferrer"
          className="author"
        >
          highman_edits
        </a>{" "}
        <img
          src="https://cdn.7tv.app/emote/01GR4G94K00003C5VKDM5EYK9S/4x.avif"
          alt="emote"
          className="emote"
        />
      </footer>
    </div>
  );
}

export default App;
