import React, { useState } from "react";
import "./App.css";

function App() {
  const [color, setColor] = useState("#00ffaa");
  const [font, setFont] = useState("Poppins");
  const [darkMode, setDarkMode] = useState(true);
  const [goalEnabled, setGoalEnabled] = useState(true);
  const [goalValue, setGoalValue] = useState(300);
  const [followers, setFollowers] = useState(245);

  const previewStyle = {
    color: color,
    fontFamily: font,
    fontSize: "3.5rem",
    fontWeight: "800",
    textShadow: darkMode ? "0 0 15px rgba(0,255,170,0.3)" : "none",
    marginTop: "15px",
  };

  const progress = Math.min((followers / goalValue) * 100, 100);

  return (
    <div className={`page ${darkMode ? "dark" : "light"}`}>
      <div className="topbar">
        <h1 className="brand">HIGHCOUNT</h1>
        <button
          className="mode-toggle"
          onClick={() => setDarkMode(!darkMode)}
        >
          {darkMode ? "‚òÄÔ∏è Light" : "üåô Dark"}
        </button>
      </div>

      <div className="panel glass">
        {/* === Color Picker === */}
        <div className="row">
          <label>Color</label>
          <input
            type="color"
            className="hex"
            value={color}
            onChange={(e) => setColor(e.target.value)}
          />
        </div>

        {/* === Font Selector === */}
        <div className="row">
          <label>Font</label>
          <select
            className="select"
            value={font}
            onChange={(e) => setFont(e.target.value)}
          >
            <option value="Poppins">Poppins</option>
            <option value="Orbitron">Orbitron</option>
            <option value="Montserrat">Montserrat</option>
            <option value="Bebas Neue">Bebas Neue</option>
            <option value="Anton">Anton</option>
            <option value="Inter">Inter</option>
          </select>
          <div className="font-preview" style={{ fontFamily: font }}>
            Preview: {font}
          </div>
        </div>

        {/* === Goal Section === */}
        <div className="row">
          <label>Show Goal:</label>
          <input
            type="checkbox"
            checked={goalEnabled}
            onChange={() => setGoalEnabled(!goalEnabled)}
          />
        </div>

        {goalEnabled && (
          <div className="row">
            <label>Goal Value</label>
            <input
              type="number"
              className="inp"
              value={goalValue}
              onChange={(e) => setGoalValue(parseInt(e.target.value) || 0)}
            />
          </div>
        )}

        {/* === Preview Count === */}
        <div style={previewStyle}>{followers}</div>

        {/* === Goal Bar Preview === */}
        {goalEnabled && (
          <div className="goal-container" style={{ marginTop: "15px" }}>
            <div className="goal-text">
              {followers} / {goalValue} followers
            </div>
            <div className="goal-bar">
              <div
                className="goal-fill"
                style={{
                  width: `${progress}%`,
                  background: color,
                }}
              ></div>
            </div>
          </div>
        )}

        <button
          className="cta"
          onClick={() => window.open("/overlay", "_blank")}
          style={{ marginTop: "20px" }}
        >
          Open Overlay
        </button>

        <p className="hint">
          Use this overlay link in OBS:{" "}
          <b>https://hightools-frontend.vercel.app/overlay</b>
        </p>
      </div>

      <footer className="credit">
        made by{" "}
        <a
          href="https://kick.com/highman-edits"
          target="_blank"
          rel="noopener noreferrer"
          className="author"
        >
          highman_edits
        </a>{" "}
        <img
          src="https://cdn.7tv.app/emote/01GR4G94K00003C5VKDM5EYK9S/4x.avif"
          alt="üíö"
          className="emote"
        />{" "}
        not affiliated with Kick
      </footer>
    </div>
  );
}

export default App;
